mapper.insert:
INSERT INTO bbs
    (title, contents, created_at, country_id, member_code)
VALUES (#{title}, #{contents}, NOW(), #{countryId}, #{memberCode});

mapper.update:
UPDATE bbs
SET view_cnt = view_cnt + 1
WHERE bbs_id = #{bbsId}

mapper.select:
SELECT country_id
FROM countries
WHERE country_name = #{countryName};
